////////////////////////////////////////////////////////////////////////
// Test_VTDWDM_IOCTL_800_GET_BUFFERED_COUNT
//
//		Test VTDWDM_IOCTL_800_GET_BUFFERED_COUNT Io Control Code
//
//
void Test_VTDWDM_IOCTL_800_GET_BUFFERED_COUNT(void)
{

	IOCTL_800_GET_BUFFERED_COUNT_STRUCT	 Struct;
	ULONG	nOutput;

	// Call device IO Control interface (VTDWDM_IOCTL_800_GET_BUFFERED_COUNT) in driver
	printf("Issuing Ioctl to device - ");
	if (!DeviceIoControl(hDevice,
						 VTDWDM_IOCTL_800_GET_BUFFERED_COUNT,
						 NULL,
						 0,
						 &Struct,
						 sizeof(Struct),
						 &nOutput,
						 NULL)
	   )
	{
		printf("ERROR: DeviceIoControl returns %0x.", GetLastError());
		Exit(1);
	}

	printf("Number of chars available in device buffer - %d", Struct.Count);
}

